doi_batch.body:
{{- range $path, $doi := site.Data.doi  }}
   {{- $path = replaceRE `/$` `` $path   }}
   {{- with site.GetPage "page" $path }}


       {{- range .Params.authors }}
         {{- with site.GetPage "taxonomyTerm" (printf "authors/%s" (urlize .))  }}
           {{- $name := split .Title " "}}

           {{ delimit (first (sub (len $name) 1) $name) " " }}
           {{ index (last 1 $name) 0 }}
           {{ with .Params.orcid }}{{ . }}{{ end }}

         {{- end }}
       {{- end }}


       {{ .Title }}


       {{ .Date.Format "02" }}
       {{ .Date.Format "01" }}
       {{ .Date.Format "2006" }}


       {{ $doi }}
       {{ printf "https://peacefulscience.org%s" $path }}


   {{- else }}
     {{- warnf "CROSSREF.PAGE:\t%s" $path }}
   {{- end }}
{{- end }}
